        axiosInstance.post('token/',{username:login, password:ppassword}).then((res) =>{
            localStorage.setItem('access_token', res.data.access);
            localStorage.setItem('refresh_token', res.data.refresh);
            localStorage.setItem('theusername', login);
            //history.push('/issues')
            if(theRedirect){
                window.location.href = 'http://127.0.0.1:3000/' + theRedirect
            }else{
                window.location.href = 'http://127.0.0.1:3000/issues'
            }
            
