import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Priority } from '../components/IssueElement'

const IssueText = (props) => {
    return (
        <>  
            <Priority priority={props.theobject.priority}/>
            <div style={{"width":"100%","height":"300px","border":"1px solid white","paddingBottom":"20px","borderRadius":"9px"}}>
                <div className='card-body'>
                    <IsSolved/><br/><br/>
                    <h4 className='card-title' style={{"width":"100%","wordWrap":"break-word"}}> {props.theobject.issue_name} </h4>
                </div>
            </div>
        </>
    );
}

const IsSolved = () => {
    return (
        <>
            <label className='btn btn-info btn-sm float-right' name='solvedIssue' style={{"marginRight":"10px","pointerEvents":"none"}}>Solved &#10003;</label>
        </>
    );
}

const Issue = () => {
    const { id } = useParams();
    const [theissue, settheissue] = useState('')

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/issue/"+String(id)).then(res => {

            if(res.data['status'] == 'success'){
                settheissue(res.data['data'])
            }
            else{
                console.log('didnt load api data')
            }
            
        })
    },[])

    useEffect(() => { 
        console.log('UPDATING VARIABLE')
        console.log(theissue);
    }, [theissue]);


    return (
        <>
            <div className='container' style={{"color":"white"}}>

            
                {(() => {
                    
                    if(theissue){
                        return (
                        <div>
                            {console.log(theissue)}
                            
                            <IssueText theobject={theissue}/>
                            
                            
                        </div>
                        );
                    }
                    
                })()}
            </div>
        </>
    )
}

export default Issue
