import React from 'react'
import { useEffect, useState } from 'react'
import axiosInstance from '../components/axiosjuicer'


const CreateNewIssue = () => {

    const [projects, setprojects] = useState([]);

    useEffect(() => {
        axiosInstance.get('projects/').then(res => {
            if(res.data['status'] === 'success'){
                setprojects(res.data['data'])
            }
        })
    }, [])


    return (
        <>
            <form>

                <h3 style={{"textAlign":"center"}}>Create an Issue :</h3>
                
                <div className="form-group">
                    <label>Title:</label>
                    <input className="form-control" name='issueText' type="text"/>
                </div>
                
                <div className="form-group">
                    <label>Description:</label>
                    <textarea className="form-control" name='text' type="text"></textarea>
                </div>
                <div className="form-group">
                    <label>Projects:</label><br/>
                    <select style='width:15%;height:36px;' name="project">

                    {(() => {
                        projects.map((aproject) => {
                            <option value={aproject.name}>{aproject.name}</option>
                        })
                    })()}

                
                        
                        
                    </select>
                </div>
                <div className="form-group">
                    <label>Priority:</label><br/>
                    <select style='width:15%;height:36px;' name="priority">
                        <option value="low">Low</option>
                        <option value="medium">Medium</option>
                        <option value="high">High</option>
                        <option value="critical">Critical</option>
                    </select>
                </div>



            </form>
        </>
    );
}

export default CreateNewIssue
