import React, { useState } from 'react'
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import auth from './auth'
import './header-sidebar.css'



const Header = (props) => {

    const [element, setElement] = useState();

    useEffect(() => {
        
        document.addEventListener("DOMContentLoaded", function(event) {
            console.log('YO');

            const showNavbar = (toggleId, navId, bodyId, headerId) =>{
            const toggle = document.getElementById(toggleId),
            nav = document.getElementById(navId),
            bodypd = document.getElementById(bodyId),
            headerpd = document.getElementById(headerId)
            
            // Validate that all variables exist
            if(toggle && nav && bodypd && headerpd){
            toggle.addEventListener('click', ()=>{
            // show navbar
            nav.classList.toggle('show')
            // change icon
            toggle.classList.toggle('bx-x')
            // add padding to body
            bodypd.classList.toggle('body-pd')
            // add padding to header
            headerpd.classList.toggle('body-pd')
            })
            }
            }
            
            showNavbar('header-toggle','nav-bar','body-pd','header')
            
            /*===== LINK ACTIVE =====*/
            const linkColor = document.querySelectorAll('.nav_link')
            
            function colorLink(){
            if(linkColor){
            linkColor.forEach(l=> l.classList.remove('active'))
            this.classList.add('active')
            }
            }
            linkColor.forEach(l=> l.addEventListener('click', colorLink))
            
            // Your code to run since DOM is loaded and ready
            });
        
        






        
        if(localStorage.getItem('theusername')){
            setElement(<><li className="nav-item active" style={{"color":"white"}}>Welcome {localStorage.getItem('theusername')}</li><li className="nav-item active">
            <a style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} href='#' onClick={()=>{localStorage.clear();window.location.reload(); }}>Logout</a>
            </li></>);
        }
        if(!localStorage.getItem('theusername')){

            setElement(<><li className="nav-item active">
                <Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/login">Login</Link>
            </li>
            <li className="nav-item active">
            <Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/register">Register</Link>
            </li></>
            );
        }
    }, []);

    

    return (

        <div>
            <div className="l-navbar" id="nav-bar">
                <nav className="nav">
                    <div> <a href="#" className="nav_logo"> <i className='bx bx-layer nav_logo-icon'></i> <span className="nav_logo-name">Bootstrap</span> </a>
                        <div className="nav_list"> <a href="#" className="nav_link active"> <i className='bx bx-grid-alt nav_icon'></i> <span className="nav_name">Dashboard</span> </a> <a href="#" className="nav_link"> <i className='bx bx-user nav_icon'></i> <span className="nav_name">Users</span> </a> <a href="#" className="nav_link"> <i className='bx bx-message-square-detail nav_icon'></i> <span className="nav_name">Messages</span> </a> <a href="#" className="nav_link"> <i className='bx bx-bookmark nav_icon'></i> <span className="nav_name">Bookmark</span> </a> <a href="#" className="nav_link"> <i className='bx bx-folder nav_icon'></i> <span className="nav_name">Files</span> </a> <a href="#" className="nav_link"> <i className='bx bx-bar-chart-alt-2 nav_icon'></i> <span className="nav_name">Stats</span> </a> </div>
                    </div> <a href="#" className="nav_link"> <i className='bx bx-log-out nav_icon'></i> <span className="nav_name">SignOut</span> </a>
                </nav>
            </div>
        </div>
            
    )
}

export default Header
