import React, { useState } from 'react'
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import auth from './auth'



const Header = (props) => {

    const [element, setElement] = useState();

    useEffect(() => {

        console.log('AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA')
        if(auth.isAuthenticated()){
            setElement(localStorage.getItem('theusername'));
            console.log('ITS TRUEEEEEEEEEE');
        }
        if(!auth.isAuthenticated()){
            setElement(<Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/login">Login</Link>);
            console.log('ITS FALSEE')
        }
    }, [localStorage.getItem('theusername')]);

    

    return (

        <div> {(() => {
            console.log('yo');
        })()}
            <nav className="navbar navbar-expand-md navbar-dark bg-dark mb-4" style={{height: 60}}>
                <div className="container-fluid">
                    <div className="nav-item active">
                        <Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/">Home</Link>
                        <Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/issues">Issues</Link>
                    </div>
                    <div>
                        <ul className="navbar-nav me-auto mb-2 mb-md-0">
                            <li className="nav-item active">
                                {element}
                            </li>
                            <li className="nav-item active">
                            <Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/register">Register</Link>
                            </li>
                            <li className="nav-item active">
                            <Link style={{textDecoration: "none",color: "white", marginLeft: 30, marginRight:30}} to="/logout">Logout</Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        </div>
            
    )
}

export default Header
