import React from 'react'

const Priority = ({priority}) => {
    return (
        <>
                <label style={{"float":"right","textTransform": "capitalize","textAlign":"left","width":"9%","marginRight":"12px","marginTop":"10px","marginLeft":"6px","fontSize":"15px","border":"1px solid white","paddingLeft":"7px","paddingRight":"7px","paddingTop":"3px","paddingBottom":"3px","borderRadius":"9px"}}>
                    {priority}

                {(() => {

                    if(priority.toLowerCase() == "low"){
                        return (
                            <span style={{"float":"right","marginLeft":"10px","marginTop":"6px","height":"13px","width":"13px","backgroundColor":"#ffff00","borderRadius":"50%","display":"inline-block"}}></span>
                        );
                    }

                    if(priority.toLowerCase() == "medium"){
                        return (
                            <span style={{"float":"right","marginLeft":"10px","marginTop":"6px","height":"13px","width":"13px","backgroundColor":"#ffbf00","borderRadius":"50%","display":"inline-block"}}></span>
                        );
                    }

                    if(priority.toLowerCase() == "high"){
                        return (
                            <span style={{"float":"right","marginLeft":"10px","marginTop":"6px","height":"13px","width":"13px","backgroundColor":"#ff8000","borderRadius":"50%","display":"inline-block"}}></span>
                        );
                    }

                    if(priority.toLowerCase() == "critical"){
                        return (                    
                            <span style={{"float":"right","marginLeft":"10px","marginTop":"6px","height":"13px","width":"13px","backgroundColor":"#ff0000","borderRadius":"50%","display":"inline-block"}}></span>
                        );
                    }
                    
                    return "fuck you";

                })()}
                
                </label>
        </>
    );
}

const IssueStatus = ({status}) => {
    return (
        <>
        {(() => {
            console.log(status);
            if(status){
                return (
                    <label style={{"float":"right","textAlign":"center","fontWeight":"bold","backgroundColor":"grey","width":"9%","marginRight":"12px","marginTop":"10px","marginLeft":"6px","fontSize":"15px","border":"1px solid white","paddingLeft":"7px","paddingRight":"7px","paddingTop":"3px","paddingBottom":"3px","borderRadius":"9px"}}>
                        Closed
                    </label>
                );
            }

            if(!status){
                return (
                    <label style={{"float":"right","textAlign":"center","fontWeight":"bold","backgroundColor":"green","width":"9%","marginRight":"12px","marginTop":"10px","marginLeft":"6px","fontSize":"15px","border":"1px solid white","paddingLeft":"7px","paddingRight":"7px","paddingTop":"3px","paddingBottom":"3px","borderRadius":"9px"}}>
                        Open
                    </label>
                );                
            }
        })()}
        </>
    );
}

const IssueElement = (props) => {
    return (
        <>
            <div style={{"cursor":"pointer","paddingTop":"9px","paddingBottom":"21px","paddingLeft":"9px","marginTop":"21px","backgroundColor":"#000000","color":"#FFFFFF","border":"1px solid white","borderRadius":"9px"}} >
            
                <Priority priority={props.priority}/>
                <IssueStatus status={props.isIssueSolved}/>
                <p style={{"fontWeight":"bold","width":"70%","wordWrap":"break-word"}}>{props.issue_name}</p>

            </div>
        </>
    )
}

export default IssueElement
