import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Priority } from '../components/IssueElement'

const IssueText = () => {
    return (
        <>
            <div style={{"float":"inherit","height":"300px","border":"1px solid white","paddingBottom":"20px","borderRadius":"9px"}}>wdwdwd</div>
        </>
    );
}

const Issue = () => {
    const { id } = useParams();
    const [theissue, settheissue] = useState('')

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/issue/"+String(id)).then(res => {

            if(res.data['status'] == 'success'){
                settheissue(res.data['data'])
            }
            else{
                console.log('didnt load api data')
            }
            
        })
    },[])

    useEffect(() => { 
        console.log('UPDATING VARIABLE')
        console.log(theissue);
    }, [theissue]);


    return (
        <>
            <div style={{"color":"white"}}>

            
                {(() => {
                    
                    if(theissue){
                        return (
                        <div>
                            {console.log(theissue)}
                            <IssueText/>
                            <Priority priority={theissue.priority}/>
                        </div>
                        );
                    }
                    
                })()}
            </div>
        </>
    )
}

export default Issue
