import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'


const Issue = () => {
    const { id } = useParams();
    const [theisue, settheisue] = useState('')

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/issue/"+String(id)).then(res => {

            if(res.data['status'] == 'success'){
                settheisue(res.data)
            }
            else{
                console.log('didnt load api data')
            }
            
        })
    },[])

    useEffect(() => { 
        console.log('UPDATING VARIABLE')
        console.log(theisue);
    }, [theisue]);


    return (
        <div>
            {console.log(id)}
        </div>
    )
}

export default Issue
